    // Вход пользователя в систему (авторизация)
    @PostMapping("/login")
    public ResponseEntity<String> login(@RequestBody UserLogin request) {
        // Получение данных из запроса
        String email = request.getEmail();
        String password = request.getPassword();

        // Проверка входных данных на корректность
        boolean email_flag = email == null || email.equals("");
        boolean password_flag = password == null || password.equals("");
        // Проверка входных данных на корректность
        if (password_flag || email_flag) {
            String error = (email_flag ? "email " : "") +
                    (password_flag ? "password " : "");
            return new ResponseEntity<>("Missing fields" + error, HttpStatus.BAD_REQUEST);
        }
        // Логика аутентификации пользователя
        var users = userService.readAll();
        User user = null;
        for (var us : users) {
            if (us.getEmail().equals(email) && us.getPassword().equals(password)) {
                user = us;
                break;
            }
        }
        if (user == null) {
            return new ResponseEntity<>("User not found", HttpStatus.NOT_FOUND);
        }

        System.out.println(user);

        // Генерация и возвращение JWT токена создание сессии

        var id_session = 0;
        var dummy = sessionService.readAll();
        if (dummy != null && dummy.size() != 0) {
            id_session = dummy.get(dummy.size() - 1).getId() + 1;
        }
        var cal = Calendar.getInstance();
        var now = new Date();
        var date = new Date(cal.getTimeInMillis() + (60 * 60 * 1000));

        String secreteKey = "secreteKey5555555555111111123123123123123123123123";
        String subject = user.getEmail() + now;
        String token = generateJwtToken(secreteKey, subject, date.getTime() - now.getTime());
        var sessions = sessionService.readAll();
        for (var ses : sessions) {
            if (Objects.equals(ses.getUser_id(), user.getId())) {
                sessionService.delete(ses.getId());
            }
        }
        sessionService.create(new Session(id_session, user.getId(), token, new Timestamp(date.getTime())));

        return new ResponseEntity<>(token, HttpStatus.OK);
    }

    // Предоставление информации о пользователе
    @GetMapping("/user")
    public ResponseEntity<String> getUserInfo(@RequestHeader("Authorization") String token) {
        // Проверка и валидация JWT токена
        var dummy = sessionService.readAll();
        Session session = null;
        for (var ses : dummy) {
            if (token.equals(ses.getToken())) {
                session = ses;
                break;
            }
        }

        var date = new Date();
        if (session == null) {
            return new ResponseEntity<>("No such token " + token, HttpStatus.NOT_FOUND);
        } else if (session.getExpires_at().getTime() - date.getTime() < 0){
            return new ResponseEntity<>("Session expires", HttpStatus.FORBIDDEN);
        }

        // Логика получения информации о пользователе
        var user = userService.read(session.getUser_id());

        // Возвращение информации о пользователе
        return new ResponseEntity<>(user.toString(), HttpStatus.OK);
    }

    public String generateJwtToken(String secretKey, String subject, long expirationTimeInMillis) {
        // Устанавливаем время истечения токена
        long now = System.currentTimeMillis();
        Date expirationDate = new Date(now + expirationTimeInMillis);

        // Генерируем токен
        JwtBuilder builder = Jwts.builder()
                .setSubject(subject)
                .setIssuedAt(new Date(now))
                .setExpiration(expirationDate)
                .signWith(SignatureAlgorithm.HS256, secretKey);

        // Возвращаем сгенерированный токен
        return builder.compact();
    }
